import { Component, OnInit, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-language-selector',
  templateUrl: './language-selector.component.html',
  styleUrls: ['./language-selector.component.scss']
})
export class LanguageSelectorComponent implements OnInit{

  @Input() current_language?: String;
  @Output() change_language = new EventEmitter<string>();

  changeLanguage(language: string) {
    this.change_language.emit(language);
  }

  constructor() { }

  ngOnInit(): void {
  }
}
<div class="language-selector">
  <img *ngIf="current_language == 'en-US'" (click)="changeLanguage('es-ES')" src="../../assets/icons/spain.svg" alt="Español" class="language-selector__flag">
  <img *ngIf="current_language == 'es-ES'" (click)="changeLanguage('en-US')" src="../../assets/icons/united-states-of-america.svg" alt="English" class="language-selector__flag">
</div>
