import { Component, OnInit, Input, OnChanges, SimpleChanges } from '@angular/core';
import language_es from "../../assets/languages/es-ES.json";
import language_en from "../../assets/languages/en-US.json";

@Component({
  selector: 'app-contact-section',
  templateUrl: './contact-section.component.html',
  styleUrls: ['./contact-section.component.scss']
})
export class ContactSectionComponent implements OnInit, OnChanges {

  @Input() language?: String;

  text = language_es.contact_section;

  constructor() { }

  ngOnInit(): void {
    this.selectLanguage();
  }

  ngOnChanges(changes: SimpleChanges) {
    this.selectLanguage();
  }

  selectLanguage() {
    if (this.language == "es-ES") {
      this.text = language_es.contact_section;
    } else if (this.language == "en-US") {
      this.text = language_en.contact_section;
    }
  }

}
<div class="contact-section" id="contact">
  <div class="contact-section__contact-form">
      <div class="contact-section__contact-form-title h1-font">
          {{text.title}}
      </div>
      <div class="contact-section__contact-form__inputs">
          <input type="text" class="contact-section__contact-form__inputs-name" placeholder={{text.name_placeholder}}>

          <input type="email" class="contact-section__contact-form__inputs-email" placeholder={{text.mail_placeholder}}>

          <textarea name="contacto" cols="30" rows="10">{{text.text_placeholder}}</textarea>

          <input type="submit" value={{text.send_button}} class="contact-section__contact-form__inputs-submit">

      </div>
  </div>
</div>
