import { Component, OnInit, Input, OnChanges, SimpleChanges } from '@angular/core';
import language_es from "../../assets/languages/es-ES.json";
import language_en from "../../assets/languages/en-US.json";

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.scss']
})
export class MenuComponent implements OnInit, OnChanges {

  @Input() language?: String;



  text = language_es.menu;
  show_list = false;

  display_menu = false;

  constructor() { }

  ngOnInit(): void {
    this.selectLanguage();
  }

  ngOnChanges(changes: SimpleChanges) {
    this.selectLanguage();
  }

  selectLanguage() {
    if (this.language == "es-ES") {
      this.text = language_es.menu;
    } else if (this.language == "en-US") {
      this.text = language_en.menu;
    }
  }

  changeVisibility() {
    this.show_list = true;
    this.display_menu = !this.display_menu;
  }

  changeWindowLocation(location: string) {
    window.location.hash = location;
    console.log("A");

  }

}
<div class="menu">
  <input type="checkbox" class="menu-checkbox" id="menu-toggle">
  <label for="menu-toggle" class="menu-label" (click)="changeVisibility()">
    <span class="menu-icon">&nbsp;</span>
  </label>
  <ul [ngClass]="{'menu__hide-list': !display_menu && show_list, 'menu__show-list': display_menu && show_list, 'menu__no-list': !show_list}" class="menu__list">
    <li class="menu__list-element h3-font"><label (click)="changeVisibility()" (click)="changeWindowLocation('#frontpage')" for="menu-toggle">{{text.frontpage}}</label></li>
    <li class="menu__list-element h3-font"><label (click)="changeVisibility()" (click)="changeWindowLocation('#experience')" for="menu-toggle">{{text.experience}}</label></li>
    <li class="menu__list-element h3-font"><label (click)="changeVisibility()" (click)="changeWindowLocation('#studies')" for="menu-toggle">{{text.studies}}</label></li>
    <li class="menu__list-element h3-font"><label (click)="changeVisibility()" (click)="changeWindowLocation('#languages_tools')" for="menu-toggle">{{text.languages_tools}}</label></li>
    <li class="menu__list-element h3-font"><label (click)="changeVisibility()" (click)="changeWindowLocation('#proyects')" for="menu-toggle">{{text.proyects}}</label></li>
  </ul>
</div>
