import { Component, OnInit, Input, OnChanges, SimpleChanges } from '@angular/core';
import language_es from "../../assets/languages/es-ES.json";
import language_en from "../../assets/languages/en-US.json";



@Component({
  selector: 'app-info-section',
  templateUrl: './info-section.component.html',
  styleUrls: ['./info-section.component.scss']
})
export class InfoSectionComponent implements OnInit, OnChanges {

  @Input() language?: String;

  text = language_es.info_section;

  constructor() { }

  ngOnInit(): void {
    this.selectLanguage();
  }

  ngOnChanges(changes: SimpleChanges) {
    this.selectLanguage();
  }

  selectLanguage() {
    if (this.language == "es-ES") {
      this.text = language_es.info_section;
    } else if (this.language == "en-US") {
      this.text = language_en.info_section;
    }
  }

}
<div class="info-section" id="languages_tools">
    <div class="info-section__card info-section__card-languages">
        <h1 class="info-section__card-text h1-font">{{text.title_languages}}</h1>
        <ul class="info-section__card-list">

            <li *ngFor="let language of text.languages" class="info-section__card-list-element p-font">
                {{language.name}}:&nbsp;
                <p *ngFor="let i of [].constructor(language.stars)">&#9733;</p>
            </li>

        </ul>
    </div>

    <div class="info-section__card info-section__card-tools">
        <h1 class="info-section__card-text h1-font info-section__card-text-tools">{{text.title_tools}}</h1>
        <ul class="info-section__card-list">
            <li *ngFor="let tool of text.tools"class="info-section__card-list-element p-font">
                {{tool.name}}:&nbsp;
                <p *ngFor="let i of [].constructor(tool.stars)">&#9733;</p>
            </li>

        </ul>
    </div>
</div>
