import { Component, OnInit, Input, OnChanges, SimpleChanges } from '@angular/core';
import language_es from "../../assets/languages/es-ES.json";
import language_en from "../../assets/languages/en-US.json";

@Component({
  selector: 'app-experience-section',
  templateUrl: './experience-section.component.html',
  styleUrls: ['./experience-section.component.scss']
})
export class ExperienceSectionComponent implements OnInit {

  @Input() language?: String;

  text = language_es.experience_section;

  constructor() { }

  ngOnInit(): void {
    this.selectLanguage();
  }

  ngOnChanges(changes: SimpleChanges) {
    this.selectLanguage();
  }

  selectLanguage() {
    if (this.language == "es-ES") {
      this.text = language_es.experience_section;
    } else if (this.language == "en-US") {
      this.text = language_en.experience_section;
    }
  }

}
<div class="experience-section">
    <div class="experience-section__jobs" id="experience">
        <h1 class="experience-section__jobs-title h1-font">{{text.title_experience}}</h1>
        <div class="experience-section__jobs-content">

            <div *ngFor="let experience of text.experiences" class="experience-section__jobs-content__box" style=--color:{{experience.color}};>
                <i class="experience-section__jobs-content__box__icon icon-basic-map"></i>
                <h3 class="u-margin-bottom-small h3-font">{{experience.title}}</h3>
                <p class="u-margin-bottom-medium experience-section__jobs-content__box__text p-font">
                  {{experience.content}}
                </p>
                <p class="experience-section__jobs-content__box__date p-font">
                  {{experience.date}}
                </p>
                <div class="experience-section__jobs-content__box__button">
                  <app-button [text]="experience.more_info" [type]="'elevate_w_url'" [link]="experience.more_info_url"></app-button>
                </div>
            </div>
        </div>
    </div>

    <div class="experience-section__studies" id="studies">
        <h1 class="experience-section__studies-title h1-font">{{text.title_studies}}</h1>
        <div class="experience-section__studies-content">

            <div *ngFor="let study of text.studies" class="experience-section__studies-content__box" style=--color:{{study.color}};>
                <i class="experience-section__studies-content__box__icon icon-basic-map"></i>
                <h3 class="u-margin-bottom-small h3-font">{{study.title}}</h3>
                <p class="u-margin-bottom-medium experience-section__studies-content__box__text p-font">
                  {{study.content}}
                </p>
                <p class="experience-section__studies-content__box__date p-font">
                  {{study.date}}
                </p>
                <div class="experience-section__studies-content__box__button">
                  <app-button [text]="study.more_info" [type]="'elevate'"></app-button>
                </div>
            </div>
        </div>
    </div>
</div>
